```cpp
#include<iostream>
#include<cstdio>
#include<cstring>
using namespace std;
const int maxn=1<<20;
struct que{//手写循环队列，其实不循环也炸不了
    int l,r,q[maxn];
    que():l(1),r(0){}
    inline void push(int x){
        q[++r&maxn-1]=x;
    }
    inline void pop(){l++;}
    inline int front(){return q[l&maxn-1];}
    inline int tail(){return q[r&maxn-1];}
    inline int size(){return r-l+1;}
    inline bool empty(){return l>r;}
}q;
int n,k[maxn],t[maxn],vis[maxn],last=1,a,sum;
int main(){
    scanf("%d",&n);
    for(int i=1;i<=n;++i){
        scanf("%d%d",&t[i],&k[i]);
        for(int j=k[i];j>0;j--){
            scanf("%d",&a);
            q.push(a);
            if(!vis[a]++)sum++;//队列没有这个数，就++
        }
        while(t[i]-86400>=t[last]){
            while(k[last]--){
                int b=q.front();
                q.pop();
                if(!--vis[b])sum--;//队列少了一种数，就--
            }
            last++;//指针后移
        }
        printf("%d\n",sum);
    }
    return 0;
}
