    for j:=tail to tail+k-1 do
    begin
      read(x);
      man[1,j]:=time;
      man[2,j]:=x;
      inc(hash[x]);
      if hash[x]-1=0 then inc(ans);
    end;
    readln;
    while time-man[1,head]>=86400 do
      begin
          dec(hash[man[2,head]]);
          if hash[man[2,head]]=0 then dec(ans);
          inc(head);
