int n,m,node[max_];//f[i][j]:第i论，庄家与主人公的顺时针距离为j
double f[max_][max_];
int get_len(int z1, int z2,int num) {
	int  t;
	if (z1 >= z2)t =  ((num + 1) - (z1 - z2 + 1) + 1);
	else  t = z2  - z1 + 1;
	 while (t > num){
		 t -= num;
	 }
	 return t;
}

signed main() {
	n = read(), m = read();
	for (int i = 1; i <= m; i++) {
		node[i] = read();
	}
	double stander = 1.0 / m;
	for (int i = 1; i <= n; i++) {
		//主人公是i
		memset(f, 0, sizeof(f));
		f[0][i] = 1.0;
		for (int j = 0; j < n; j++) {
			//游戏进行到第j轮
			for (int z = 1; z <= n; z++) {
				//庄家与主人公的距离为z
				if (f[j][z] < eps)continue;
				int num = n - j;
				for (int x = 1; x <= m; x++) {
					//枚举所有牌
					int wei = node[x];
					while (wei > num){
						wei -= num;
					}
					if (wei == z)continue;
					wei++;
					while (wei > num ) {
						wei -= num ;
					}
					int len = get_len(wei, z, num);
					f[j + 1][len] += stander * f[j][z];
				}
			}
		}
		printf("%.2lf", f[n - 1][1] * 100);
		cout << '%';
		if (i != n)cout << " ";
	}
	return 0;
}
