{
    scanf("%d%d",&n,&m);
    for(int i=1;i<=m;i++)
    {
        scanf("%d",&a[i]);
    }
    f[1][1]=1;
    for(int i=2;i<=n;i++)
    {
        for(int j=1;j<=m;j++)
        {
            int temp=a[j]%i;
            if(!temp)
            {
                temp=i;
            }
            for(int k=1;k<i;k++)
            {
                temp++;
                if(temp>i)
                {
                    temp=1;
                }
                f[i][temp]+=f[i-1][k]*(double)1.0/(double)m;
            }
        }
    }
    for(int i=1;i<=n;i++)
    {
        printf("%.2lf%% ",f[n][i]*100.0);
    }
    return 0;
}

