#include<bits/stdc++.h>
using namespace std;
struct node                              //节点
{
    int data;
    node \*next;
}\*head;                                   //头指针
void create(int i)                    //创建链表
{
    head=new node;
    head->next=NULL;
    node \*p,\*q;
    int a;
    p=head;
    q=new node;
    a=i;
    while(a--)
    {
        q->data=i-a;
        p->next=q;
        p=q;
        q=new node;
    }
    p->next=NULL;
}
node \*getnode(int i)           //寻找第i个节点
{
    node \*p;
    p=head;
    while(i--)
        p=p->next;
    return p;
}
int main()
{
    int i,j,n,m,s,h=1;
    node \*sum,\*num;
    cin>>n>>m;
    create(n);
    for(i=1;i<=m;i++)
    {
            s=i\*i\*i%5+1;
            if(h+s<=n-i+1)
            h+=s;
            else
            h=s+1;
               sum=getnode(h);                   //记数据
            num=getnode(h-1);               //删节点
            num->next=sum->next;
            sum->next=NULL;
    }
    cout<<sum->data;
}
