#include<bits/stdc++.h>
using namespace std;
vector<int> G[4010];//vector存图
int mat[4010];
bool vis[4010];
bool dfs(int u)
{
	for(int i=0;i<G[u].size();i++)
	{
		int v=G[u][i];
		if(!vis[v])
		{
			vis[v]=1;
			if(!mat[v]||dfs(mat[v]))
			{
				mat[v]=u;
				return 1;
			}
		}
	}
	return 0;
}
int maxmatch(int m)//匈牙利模板
{
	int ans=0;
	for(int i=1;i<=m;i++)
	{
		memset(vis,0,sizeof(vis));
		ans+=dfs(i);
	}
	return ans;
}
int main()
{
	int n,m;//n为人数，m为排数
	cin>>m;
	n=2*m;//每排两人
	int a,b;
	for(int i=1;i<=n;i++)
	{
		cin>>a>>b;
		G[i].push_back(a);
		G[i].push_back(b);
		G[i].push_back(a+m);
		G[i].push_back(b+m);
	}
	cout<<maxmatch(m*2);//注意了，已经把每排拆成了两排，所以匹配时m*2
}
