把椅子拆成两个，就变成二分图匹配了。
```cpp
/*Tokgo*/
#include <bits/stdc++.h>



using namespace std;

#define N 50000

int n , m , c ; 
int x , y ; 

int head[ N ] , tot ; 
struct edge{ int y , nt ; } e[ N ] ;
void add_edge( int x , int y ) {
	 e[ ++tot ] = (edge){ y , head[ x ] } ; 
	 head[ x ] = tot ; 
}

bool did[ N ] ; 
int link[ N ] ; 

bool dfs( int x ) {
	for( int i = head[x] ; i ; i = e[i].nt ) {
		int to = e[i].y ;
		if( did[to] ) continue ; 
		did[to] = 1 ; 
		if( link[to] == -1 or dfs( link[to] ) ) {
			link[ to ] = x ; 
			return true ; 
		}
	}
	return false ; 
}

int ans ;
void match( int n ) {
	memset( link , -1 , sizeof( link ) ) ;
	for( int i = 1 ; i <= n ; ++ i ) {	
		memset( did , 0 , sizeof( did ) ) ;
		if( dfs( i ) ) ans ++ ; 
	}
}

int main() {

    cin >> n ; 
    for( int i = 1 ; i <= n * 2 ; ++ i ) {
    	cin >> x >> y ; 
    	add_edge( i , x + 2*n ) , add_edge( i , x + 3*n ) ;
    	add_edge( x + 2*n  , i ) , add_edge( x + 3*n , i ) ; 
    	add_edge( i , y+2*n ) , add_edge( i , y + 3*n ) ;
    	add_edge( y+2*n , i ) , add_edge( y + 3*n , i ) ;
	}
	match( 2 * n ) ;
	cout << ans << endl ;

    return 0;
}


