    return -x;
}
int find(int x)
{
    if(fat[x]!=x)fat[x]=find(fat[x]);
    return fat[x];
}
void unionn(int x,int y)
{
    int r1=find(x);
    int r2=find(y);
    if(r1>r2)swap(r1,r2);
    if(r1!=r2)
    {
        fat[r2]=r1;
        root[r1]+=root[r2];
    }
}
int main()
{
    scanf("%d%d%d%d",&n,&m,&p,&q);
    for(int i=1;i<=n+m;i++)//初始每个人的父亲为自己
        fat[i]=i;
    for(int i=1;i<=n+m;i++)//初始每个人认识的人为1（自己）
        root[i]=1;
    for(int i=1;i<=p;i++)
    {
        int x,y;
        scanf("%d%d",&x,&y);
        unionn(x,y);
    }
    for(int i=1;i<=q;i++)
    {
        int x,y;
        scanf("%d%d",&x,&y);
        unionn(Abs(x)+n,Abs(y)+n);
    }
    printf("%d\n",min(root[1],root[1+n]));
    return 0;
}
