    prea[x]=scha(prea[x]);
    return prea[x];
}
int schb(int x)
{
    if (preb[x]==x) return preb[x];
    preb[x]=schb(preb[x]);
    return preb[x];
}
void joina(int x,int y)
{
    prea[scha(x)]=scha(y);
}
void joinb(int x,int y)
{
    preb[schb(x)]=schb(y);
}
int main()
{
    cin>>n>>n1>>m>>m1;
    for (i=1;i<=max(n,n1);i++)
    {
        prea[i]=i;
        preb[i]=i;
