    for (i=1;i<=m;i++)
    {
        cin>>a>>b;
        joina(a,b);
    }
    for (i=1;i<=m1;i++)
    {
        cin>>a>>b;
        a=abs(a);
        b=abs(b);
        joinb(a,b);
    }
    for (i=1;i<=n;i++)
    {
        if (scha(i)==scha(1)) suma++;
    }
    for (i=1;i<=n1;i++)
    {
        if (schb(i)==schb(1)) sumb++;
    }
    if (suma<sumb) cout<<suma; else cout<<sumb;
}
