using namespace std;
int f1[20010],f2[20010],a,b,fx,fy,q,n,m,p,male=0,fe=0;
void ms(int n,int f[]){
	for(int i=1;i<=n;i++)f[i]=i;
	return;
}
int find(int x,int f[]){
	if(x==f[x])return x;
	else f[x]=find(f[x],f);
	return f[x];
}
void merge(int fx,int fy,int f[]){
	if(fx<fy){
		f[fy]=fx;
	}else{
		f[fx]=fy;
	}
	return;
}
int main(){
	cin>>n>>m>>p>>q;
	ms(n,f1);
	ms(m,f2);
