{
    int tmp=x;
    while(tmp!=fa[tmp]) tmp=fa[tmp];
//    int i=x,j;
//    while(fa[i]!=i)
//    {
//        j=fa[i];
//        fa[i]=tmp;
//        i=j;
//    }
    return tmp;
} 
int main()
{
    cin>>n>>m>>p>>q; 
    for(int i=1;i<=n;i++)
      A[i]=i;
    for(int i=1;i<=m;i++)
      B[i]=i;
    for(int i=1;i<=p;i++)
    {
        cin>>a>>b;
        if(Search(a,A)==1) swap(a,b);
        A[Search(a,A)]=Search(b,A);
    }
    for(int i=1;i<=q;i++)
    {
        cin>>a>>b;
        if(Search(-a,B)==1) swap(a,b);
        B[Search(-a,B)]=Search(-b,B);
    }
    ans=0;
    for(int i=1;i<=n;i++)
      if(Search(i,A)==1) ans++;
    int tmp=0;
    for(int i=1;i<=m;i++)
      if(Search(i,B)==1) tmp++;
    cout<<min(ans,tmp);
    return 0;
}
