#include<cstdio>
#include<cstring>
#include<algorithm>
using namespace std;
const int MAXN=40;
inline int read()
{
	int s=0,w=1;
	char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-')w=-1;ch=getchar();}
	while(ch>='0'&&ch<='9')s=s*10+(ch-'0'),ch=getchar();
	return s*w;
}
int n,v,a[MAXN],b[MAXN],ans=1e9;
int abs(int x)
{
	if(x<0) x=-x;
	return x;
}
void dfs(int i,int sum_a,int sum_b)
{
	if(i>n)
	{
		if(sum_a+sum_b>=v) ans=min(ans,abs(sum_a-sum_b));
		return;
	}
	if(!ans) return;
	dfs(i+1,sum_a,sum_b);
	dfs(i+1,sum_a+a[i],sum_b+b[i]);
	return;
}
int main()
{
	n=read(),v=read();
	for(register int i=1;i<=n;i++) a[i]=read(),b[i]=read();
	dfs(1,0,0);
	if(ans==1e9) puts("-1");
	else printf("%d\n",ans);
	return 0;
}
