#include<bits/stdc++.h>
using namespace std;
int f[100001],tot;
struct A{
    int w,v;
}a[101];
bool cmp(A i,A j){
    return i.w>j.w;
}
int main(){
    int N,V,ans=0x3f3f3f;
    cin>>N>>V;
    for(int i=1;i<=N;i++){
        int c,b;
        cin>>c>>b;
        a[i].v=b-c;
        a[i].w=c+b;
        if(c+b>0) 
        tot+=a[i].w;
    }
    if(tot<V) {cout<<"-1";return 0;}
    memset(f,0x3f3f3f,sizeof(f));
    f[0]=0;
    sort(a+1,a+N+1,cmp);
    for(int i=1;i<=N;i++){
        for(int j=tot;j>=a[i].w;j--){
            if(abs(f[j-a[i].w]+a[i].v)<abs(f[j])){
