                 }
            }
        }
    }
    for(int i=V;i<=tot;i++){
         if(abs(f[i])<ans) ans=abs(f[i]);
    }
    if(ans==0x3f3f3f) cout<<"-1";
    else cout<<ans<<endl;
    return 0;
} 
