#include<iostream>
#include<cmath>
using namespace std;
int n,v,a[30],b[30],minn=100000,m,h; ///minn一开始要设定一个很大的值，不然就会产生永久的cout<<"-1"<<endl; 导致程序结果错误 
bool visited[30];//判断是否做过这件事
void dfs(int step)
{
	if(m+h>v) ///当亲密值到达v的时候，就到了一起 
		minn=min(abs(m-h),minn); ///计算差值 这个差值计算得有个限制，把计算放在这个if里面是最优性剪枝 
	if(minn==0) ///如果已经好到了极点，那么绝对值只差应该为 0，这个就是最终的状态
		return;
        for(int i=step;i<n;i++)
	{
    	        if(visited[i]==0)
		{
    		      visited[i]=1;
    		      m+=a[i];
    		      h+=b[i];
    		      dfs(i+1);
		         visited[i]=0;
    		      m-=a[i];
    		      h-=b[i];	
		}
	}
}
int main()
{
	cin>>n>>v;
	for(int i=0;i<n;i++)
		cin>>a[i]>>b[i];
	dfs(0);
	if(minn==100000)
	{///亲密值未发生改变 没有在一起   
		cout<<"-1";
		return 0;
	}
	cout<<minn;
	return 0;
}
