ll a,b,d=0,l=0;
struct qujian{
	ll a,b;
	qujian(ll a=0,ll b=0):a(a),b(b){}
	bool operator <(const qujian& p)const{
		return a<p.a||(a==p.a&&b<p.b);
	}
};
set<qujian>q;
int main(){
	cin>>n;
	for(int i=1;i<=n;i++){
		scanf("%lld%lld",&a,&b);
		q.insert(qujian(a,b)); 
	}
	for(set<qujian>::iterator it=q.begin();it!=q.end();++it){
		qujian p=*it;
		if(p.b<=d) continue;
		if(p.a<=d)l+=p.b-d;
		else l+=p.b-p.a+1;
		d=p.b;
	}
