	scanf("%d", &n);//输入克数 
	if(n > 30){
		printf("0");//若n>30则输出0即可
		return 0;//再见！ 
	}
	count_ans(n, 0);//统计答案数 
	printf("%d\n", ans);//输出答案 
	print_method(n, 0);//输出方案 
