按题意BFS

```cpp
#include<iostream>
#include<cstdio>
#include<algorithm>
#include<cmath>
#include<cstring>
#include<queue>
#include<map>
using namespace std;
int n,ans=0x3f3f3f3f;
struct node{
	int x,y,step;
};
queue<node> q;
map<pair<int,int>,int> m;
int bfs(){
	q.push((node){1,1,0});
	m[make_pair(1,1)]=1;
	while(!q.empty()){
		node tmp=q.front();
		q.pop();
		if(tmp.x==n||tmp.y==n){
			return tmp.step;
		}
		if(tmp.step>ans) continue;//最优性剪枝 
		if(tmp.x>n || tmp.y>n) continue;//可行性剪枝 
		if(!m[make_pair(tmp.x+tmp.y,tmp.y)]){
			q.push((node){tmp.x+tmp.y,tmp.y,tmp.step+1});
			m[make_pair(tmp.x+tmp.y,tmp.y)]=1;
		}
		if(!m[make_pair(tmp.x,tmp.x+tmp.y)]){
			q.push((node){tmp.x,tmp.x+tmp.y,tmp.step+1});
			m[make_pair(tmp.x,tmp.x+tmp.y)]=1;
		}
	}
}
int main(){
	cin>>n;
	cout<<bfs()<<endl;
	return 0;
}
```

