#include<iostream>
using namespace std;
int minn;
int step;
int minnn=999;
void asd(int g,int h){
    if(g==h&&h==1){
        if(minn>step)minn=step;
        return;
    }
    step++;
    if(step>minn){
        step--;
        return;
    } 
    if(g==h){
        step--;
        return;
    }
    if(g>h){
        asd(g-h,h);
        step--;
    }
    if(g<h){
        asd(g,h-g);
        step--;
    }
    return;
}
int main(){
    int n;
    cin>>n;
    for(int i=1;i<=n;i++){
        minn=999;
        step=0;
        asd(i,n);
        if(minn<minnn)minnn=minn;
    }
    cout<<minnn<<endl;
    return 0;
}
