#include<bits/stdc++.h>
using namespace std;
struct yh
{
	int a;
	int b;
}lr[300];//a为食物脂肪，b为种类
int r[300],ans[300];//r为食物种类上限，ans为可食用的满足要求的最好的食物
bool cmp(yh x,yh y)
{
    if(x.b!=y.b)
	{
		return x.b<y.b;
	}
    if(x.b==y.b)
    {
    	return x.a>y.a;
    }
}//排序函数：先按种类排再按食物脂肪从大到小排序
int main()
{
	int m,n,da=0,k,ji=1,e=1;//n为食物总数，m为可吃的量，k为食物种类
	cin>>n>>m>>k;
	for(int i=1;i<=k;i++)
	{
		cin>>r[i];
	}
	for(int i=1;i<=n;i++)
	{
		cin>>lr[i].a>>lr[i].b;
	}
	sort(lr+1,lr+n+1,cmp);//排序
	for(int i=1;i<=n;i++)
	{
		if(r[ji]!=0)
		{
			ans[e]=lr[i].a;
			e++;//e为满足要求的最好食物数量
			r[ji]--;
		}//如果食物在可食用且未达到上限时则存入答案数组中
		if(lr[i].b!=lr[i+1].b)
		{
			ji++;
		}//ji统计算到了哪种
	}
	e=e-1;//之前会多算一个，减回来
	sort(ans+1,ans+e+1);//将满足要求食物质量排序
	for(int i=1;i<=m;i++)
	{
		da+=ans[e];//该食物被选
		e--;//普通sort为从小到大排，懒的写cmp将就一下
	}
