```cpp
#include<bits/stdc++.h>
using namespace std;
int n,m,k,ans=0;
struct bzyq{
    int a,b;
}
s[470];//用结构体排序好排
int x[470];
bool cmp(bzyq i,bzyq j)
{
    return i.a>j.a;//根据脂肪值排序
}
int main()
{
    cin>>n>>m>>k;
    for(int i=1;i<=k;i++)
     cin>>x[i];
    for(int i=1;i<=n;i++)
     cin>>s[i].a>>s[i].b;
    sort(s+1,s+n+1,cmp);
    int i=1;
    while(m&&i<=n)//m得>0
    {
        if(x[s[i].b]>0)
        {
