```cpp
#include<algorithm>
#include<iostream>
#include<cstring>
#include<cstdio>
#include<cmath>
using namespace std;
int m,n,sum,a[20010];
int main()
{
    int t=0;
    scanf("%d%d",&m,&n);
    for(int i=1;i<=n;i++)
        a[i]=-100;
    for(int i=1;i<=m;i++)
        for(int j=1;j<=n;j++)
        {
            int x;
            scanf("%d",&x);
            if(x>a[j])
                a[j]=x;//贪心的策略，取每列最大值 
        }
    for(int i=1;i<=n;i++)
    {
        if(a[i]>=0)
        {
            t+=a[i];//若为正数，累加入t 
            if(t>sum)
                sum=t;//更新sum的值 
        }
        else//若为负数 
        {
            if(t+a[i]<=0)//若累加后小于零 
                t=0;//重新累加 
            else
                t+=a[i];//累加 
        }
    }
    printf("%d\n",sum);
    return 0;
}
