 ```cpp
#include <iostream>
#include <cstdio>
#include <cmath>
using namespace std;
typedef long long ll;
ll l, r;
ll ans = 0;
ll pnum(ll n){ // 统计位数
	ll x = n, sum = 0;
	while(x > 0){
		x = x / 10;
		sum++;
	}
	return sum;
}
inline ll geth(ll n){ // 获取最高位
	return n / pow(10, pnum(n) - 1);
}
inline ll getl(ll n){ // 获取最低位
	return n % 10;
}
int main()
{
	scanf("%lld%lld", &l, &r);
	if(l == r){ // 这是一个特判，l和r相等的情况
		if(getl(l) == geth(l)){
			printf("1");
			return 0;
		}
		else{
			printf("0");
			return 0;
		}
	}
	if(l < 10){
		if(r < 10){
			ans = ans + r - l + 1;
			printf("%lld", ans);
			return 0;
		}
		ans = ans + 9 - l + 1;
		l = 10;
	}
	if(getl(l) <= geth(l)) ans++;
	if(getl(r) >= geth(r)) ans++;
	l = l + 10 - (l % 10);
	r = r - (r % 10);
	ans = ans + (r / 10 - l / 10);
	printf("%lld", ans);
	return 0;
}

