char ch;
int n,q,nd,nc,cd[6],cc[6];
double p,ans=0;
double ksm(double a,int b)
{
    double num=1;
    for (;b;a=a*a,b>>=1)
     if (b&1) num=num*a;
    return num;
}
int main()
{
    scanf("%d %lf %d\n",&n,&p,&q);
    if (n<=50)
    {
        q=n-q;
        while ((ch=getchar())!='0'&&ch!='1') ch=getchar();
        while (ch=='0'||ch=='1')
        {            
            if (ch=='1') nd++;
            if (ch=='0') nc++;
