        cd[0]=1, cd[1]=nd, cd[2]=cd[1]*(nd-1)/2, cd[3]=cd[2]*(nd-2)/3, cd[4]=cd[3]*(nd-3)/4,cd[5]=cd[4]*(nd-4)/5;
