```cpp

#include<iostream>
#include<cstdio>
#include<cstring>
#include<cmath>
#include<algorithm>
#define ll long long
using namespace std;
inline ll read()
{
    int x=0,f=1;
    char ch=getchar();
    while(ch<'0'||ch>'9'){if(ch=='-')f=-1; ch=getchar();}
    while(ch>='0'&&ch<='9'){x=x*10+ch-'0'; ch=getchar();}
    return x*f;
}
int n,m,a[100005],b[100005],f[35],ans,tot;
int cal(int x)
{
    for(int i=1;i<=n;i++)
        if(a[i]==1)x=(x&b[i]);
        else if(a[i]==2)x=(x|b[i]);
        else x=(x^b[i]);
    return x;
}
int main()
{
    n=read();m=read();
    for(int i=1;i<=n;i++)
    {
        char ch[5];
        scanf("%s",ch);
        if(ch[0]=='A')a[i]=1;
        else if(ch[0]=='O')a[i]=2;
        else a[i]=3;
        b[i]=read();
    }
    int t=cal(0);
    for(int i=0;i<=30;i++)f[i]=(cal(1<<i)&(1<<i));
    for(int i=30;i>=0;i--)
    {
        if((1<<i)&t)ans+=(1<<i);
        else if((tot+(1<<i)<=m)&&f[i])
        {
            tot+=f[i];
            ans+=f[i];
        }
    }
    printf("%d",ans);
    return 0;
}

