{
	cin>>n>>Q;
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++)
		{
			cin>>t;
			if(i==j)
			{
				ans+=t;
				ans%=2;
			}
		}
	while(Q--)
	{
		cin>>a;
		if(a==1||a==2)
		{
			cin>>b;
			ans+=1;
			ans%=2;
		}
		else
			cout<<ans;
	}
	return 0;
