program hrn;
 var
  p,q,s:double;
  a,b,l,i,j,x,y:longint;
 begin
  read(a,b,l);
  q:=a/b;s:=maxlongint;  //初始化Q和S
  for i:=1 to l do
   for j:=1 to l do begin  //开始枚举
    p:=i/j;
    if(p>=q)and(p-q<s)then begin //注意，一定要是p>=q
     s:=p-q;
     x:=i;y:=j;
    end;                 //寻找到更小的P-Q时，更新S和答案
   end;
  write(x,' ',y);
 end.

