```cpp
#include<iostream>
#include<cstdio>
#include<algorithm>
using namespace std;
int n,m,d;
int v[40001][5]={0};
int num[15001]={0};    //出现相同数的次数
int data[40001];
int main()
{
    int i,w;
    scanf("%d%d",&n,&m);
    for(i=1;i<=m;i++){
        scanf("%d",&data[i]);
        num[data[i]]++;
    }
    //开始枚举,a b c d, d-c=w, b-a=2w, c-d>6w
    for(w=1;w*9<n;w++){
        int sum = 0;
        int a,b,c,d;
        //枚举d的位置
        for(d = w * 9 + 2; d <= n; d++){
            a = d - 9 * w - 1;    //a的魔法值
            b = d - 7 * w - 1;    //b的魔法值
            c = d - w;            //c的魔法值
            sum += num[b] * num[a]; //计算前缀和可以组成的魔法阵的个数
            v[c][3] += num[d] * sum;
            v[d][4] += num[c] * sum;
        }
        sum = 0;
        //枚举a的位置
        for(a = n - w * 9 - 1; a >= 1; a--){
            b = a + 2 * w;
            c = a + w * 8 + 1;
            d = a + w * 9 + 1;
            sum += num[c] * num[d];
            v[a][1] += num[b] * sum;
            v[b][2] += num[a] * sum;
        }
    } 
    for(i=1;i<=m;i++){
        printf("%d %d %d %d\n",v[data[i]][1],v[data[i]][2],v[data[i]][3],v[data[i]][4]);
    }
    return 0;
}

