inline bool cmp(fac a,fac b){return a.dis<b.dis;}
inline long long need(long long x,long long y)
{
	long long ret=0;
	for(long long i=x;i<=y-1;++i)ret+=(a[y].dis-a[i].dis)*a[i].num;
