### 【代码】
```cpp
#include<bits/stdc++.h>
using namespace std;
const int N=20000+10;
typedef long long ll;//本题要longlong 否则过不去（惨痛的经验）
struct Node{
    ll a,b;
}p[N];//p数组来存放a,b，可以供排序
ll read()//读优
{
	ll s=0,base=1;
	char c;
	while(!isdigit(c=getchar()))if(c=='-')base=-base;
	while(isdigit(c)){s=s*10+(c^48);c=getchar();}
	return s*base;
}
bool cmp(Node poj,Node bzoj)
{
    return min(poj.a,bzoj.b)<min(bzoj.a,poj.b);
}//这个是本题关键 这里不细说
int main()
{
    ll T=read();
    while(T--)
    {
        ll n=read(),c[N];
        for(ll i=1;i<=n;i++) p[i].a=read(),p[i].b=read();//读入
        sort(p+1,p+1+n,cmp);//排序 贪心体现
        ll s=0;c[0]=0;
        for(ll i=1;i<=n;i++)
        {
            s+=p[i].a;
            c[i]=max(c[i-1],s)+p[i].b;//图上式子的实现
        }
        printf("%lld\n",c[n]);//输出
    }
    return 0;
}
```
