int dfs(int l,int r,int k) {
	if (dp[l][r][k]) return dp[l][r][k];
	if (l == r) return (cnt[l] + k) * (cnt[l] + k);
	dp[l][r][k] = dfs(l,r - 1,0) + (cnt[r] + k) * (cnt[r] + k);
	for (int i = l ; i < r ; ++ i) {
		if (a[i] == a[r]) dp[l][r][k] = Max(dp[l][r][k],dfs(l,i,cnt[r] + k) + dfs(i + 1,r - 1,0));
	}
	return dp[l][r][k];
}

