                a[i][j].gd=0;
        for(int i=1;i<=n;i++)
        {
            s=read(),q=read();
            td();fg(101,101);
        }
        for(int i=1;i<=200;i++)
            for(int j=1;j<=200;j++)
                if(a[i][j].gd)
                    mp[a[i][j].s]++;
        for(int i=1;i<=6;i++) cout<<mp[i]<<" ";
