void quickSort(int l, int r) {
    if (l >= r)return;
    int i = l, j = r, base = arr[l];
    while (i < j) {
        while (i < j && arr[j] >= base)j--;
        while (i < j && arr[i] <= base)i++;
        if (i < j)swap(&arr[i], &arr[j]);
    }
    swap(&arr[i], &arr[l]);
    quickSort(l, i - 1);
    quickSort(i + 1, r);
}

