class BigInteger
{
public:

int size,num[1000];//size代表数的长度，num存大整型数





```cpp
    BigInteger()//定义时自动清零
        {
            size=0;
            memset(num,0,sizeof(num));
        }
    BigInteger(int data)
        {
            size=0;
            while (data!=0)
            {
                size++;
                num[size]=data%10;
                data=data/10;
            }
        }
    void init(int data)//用来给大整型变量赋整型值
        {
            size=0;
            while (data!=0)
            {
                size++;
                num[size]=data%10;
                data=data/10;
            }
        }
};
BigInteger operator + (BigInteger A,BigInteger B)//高精度加法
{
    BigInteger Ans;
    int s=max(A.size,B.size);
    Ans.size=s;
    for (int i=1;i<=s;i++)
        Ans.num[i]=A.num[i]+B.num[i];
    for (int i=1;i<=s;i++)
        if (Ans.num[i]>=10)
        {
            Ans.num[i+1]+=Ans.num[i]/10;
            Ans.num[i]=Ans.num[i]%10;
        }
    if (Ans.num[s+1]!=0)
        Ans.size++;
    return Ans;
}
BigInteger operator - (BigInteger A,BigInteger B)//高精度减法
{
    BigInteger Ans;
    int s=max(A.size,B.size);
    Ans.size=s;
    for (int i=1;i<=s;i++)
        Ans.num[i]=A.num[i]-B.num[i];
    for (int i=1;i<=s;i++)
        if (Ans.num[i]<0)
        {
            Ans.num[i+1]-=Ans.num[i]/10;
            Ans.num[i]=Ans.num[i]%10;
        }
    if (Ans.num[s+1]!=0)
        Ans.size++;
    return Ans;
}
ostream & operator << (ostream &os,BigInteger A)//用于大整型数的输出（只能用cout）
{
    int s=A.size;
    for (int i=s;i>=1;i--)
        os<<A.num[i];
    return os;
}
BigInteger operator * (BigInteger A,BigInteger B)//高精度乘法
{
    BigInteger Ans;
    int s1=A.size,s2=B.size;
    for (int i=1;i<=s1;i++)
        for (int j=1;j<=s2;j++)
            Ans.num[i+j-1]+=A.num[i]*B.num[j];
    int s=s1+s2-1;
    int k=1;
    while ((Ans.num[k]!=0)||(k<=s))
    {
        Ans.num[k+1]+=Ans.num[k]/10;
        Ans.num[k]=Ans.num[k]%10;
        k++;
    }
    if (Ans.num[k]==0)
        k--;
    Ans.size=k;
    return Ans;
}
BigInteger Ans,Ans1,Ans2;//定义大整型数
il int gi()
{
   int x=0;
   short int t=1;
   char ch=getchar();
  while((ch<'0'||ch>'9')&&ch!='-') ch=getchar();
  if(ch=='-') t=-1,ch=getchar();
  while(ch>='0'&&ch<='9') x=x*10+ch-48,ch=getchar();
  return x*t;
}
int main()
{
    Ans1.init(1);//把Ans1赋值为1,不能写Ans1=1！！！
    Ans2.init(3);//把Ans2赋值为3,不能写Ans2=3！！！
    int n=gi();
    for(int i=3;i<=n;i++) if(i%2) Ans1=Ans1+Ans2;
        else Ans2=Ans2+Ans1;
    if(n%2) Ans=Ans1;
    else Ans=Ans2;//求f[n]
    if(n%2) Ans=Ans*Ans;
    else Ans=Ans*Ans-4;//求F[n]
    cout<<Ans<<endl;
    return 0;
}
```

# ~~遇到这种题我只会打表~~

设f[i]表示n=i时的答案，~~打表可得~~ ：

	f[1]=1,f[2]=5,f[3]=16,

   	f[i]=3*f[i-1]-f[i-2]+2 


 （怎么感觉自己的公式比别人的简单好多啊）

 再套高精度即可。。。

 打表代码如下：

 ```cpp
 #include<bits/stdc++.h>
using namespace std;
#define N 107
#define M 13
#define ll long long
const ll bit=1e9;
struct edge
{
	int x,y;
}e[N];
int tot,fa[N],n,ans;
int find(int x)
{
	return x==fa[x]?x:fa[x]=find(fa[x]);
}
void dfs(int x,int d)
{
	if(d+tot-x+1<n)return;
	if(d>=n){ans++;return;}
	dfs(x+1,d);
	int a=e[x].x,b=e[x].y;
	int u=find(a),v=find(b);
	if(u!=v)
	{
		int tmp[N];
		memcpy(tmp,fa,sizeof(fa));
		fa[u]=v;
		dfs(x+1,d+1);
		memcpy(fa,tmp,sizeof(fa));
	}
}
int main()
{
	int i;
	for(n=1;n<=10;n++)
	{
		ans=0;tot=0;
		for(i=1;i<n;i++)
			e[++tot]={i,i+1};
		e[++tot]={n,1};
		for(i=1;i<=n;i++)
			e[++tot]={i,n+1};
		for(i=1;i<=n+1;i++)
			fa[i]=i;
		dfs(1,0);
		printf("%d\n",ans);
	}
	return 0;
}
```

然后是AC的代码：
```cpp
// luogu-judger-enable-o2
#include<bits/stdc++.h>
using namespace std;
#define ll long long
#define N 107
#define M 13
const ll bit=1e9;
struct nb
{
    ll a[M];
    int l;
    nb(){memset(a,0,sizeof(a)),l=0;}
    ll &operator [](int x){return a[x];}
    void operator =(int x){a[0]=x;}
    void operator +=(int x)
    {
        int i=0;
        a[0]+=x;
        while(a[i]>=bit)
        {
            a[i]-=bit;
            a[i+1]++;
            i++;
        }
        if(a[l+1])l++;
    }
    void operator *=(int x)
    {
        int i=0;
        ll rest=0;
        for(i=0;i<=l;i++)
        {
            a[i]=a[i]*x+rest;
            rest=a[i]/bit;
            a[i]%=bit;
        }
        if(rest)a[++l]=rest;
    }
    void operator -=(nb b)
    {
        int i=0;
        for(i=0;i<=b.l;i++)
        {
            a[i]-=b[i];
            if(a[i]<0)a[i]+=bit,a[i+1]--;
        }
        while(a[i]<0)a[i]+=bit,a[i+1]--,i++;
        while(l>0&&a[l]==0)l--;
    }
    void print()
    {
        printf("%lld",a[l]);
        for(int i=l-1;i>=0;i--)
            printf("%09lld",a[i]);
    }
}f[N];
