using namespace std;

const int MAXN = 105;

__int128 f[MAXN] = {0, 1, 3};

__int128 a, b, ans;
int n;

inline void Print(__int128 x)
{
    if (x > 9)
        Print(x / 10);
    putchar(x % 10 + '0');

    return;
}

signed main(void)
{
    cin >> n;
    for (int i = 3; i <= n; i++)
        f[i] = f[i - 1] + f[i - 2];

    if (n == "naive!")
    {
        puts("不推荐打表通过此题，代码仅仅展现__int128的应用");
        return 0;
    }

    ans = f[n] * f[n] - 4 * (n % 2 == 0);

    Print(ans);

    return 0;
