    f[1].shu[0]=1;f[1].len=1;
    int a;
    for(int i=2;i<=n;i++)
    {
        for(int j=1;j<i;j++)
        {
        	a=i-j;
            f[i]=f[i]+(f[j]*a);
        }
    }
    for(int i=1;i<=n;i++)
    {
    	a=(n-i+1)*(n-i+1);
        dp[i]=f[i]*a;
    }
    ans.shu[0]=0;ans.len=1;
    for(int i=1;i<=n;i++)ans=ans+dp[i];
    for(int i=ans.len-1;i>=0;i--)printf("%d",ans.shu[i]);
