		if(s1[tmp+i]<s2[i]+cf) {  
			s=char(s1[tmp+i]-s2[i]-cf+'0'+10)+s; 
			cf=1;  
		} else {  
			s=char(s1[tmp+i]-s2[i]-cf+'0')+s;  
			cf=0;  
