	}  
	for(int i=tmp-1; i>=0; i--) {  
		if(s1[i]-cf>='0') {  
			s=char(s1[i]-cf)+s;  
			cf=0;  
		} else {  
			s=char(s1[i]-cf+10)+s;  
			cf=1;  
		}  
	}  
	s.erase(0,s.find_first_not_of('0'));  
	return s;  
}  
string mul(string s1,string s2) {  
	string s;  
	int sz1=s1.length();  
	int sz2=s2.length();  
