	{
		ret.ilen++;
	}
	return ret;
}
Bignum mul(Bignum x)
{
	Bignum ret=zero;
	for(int i=1;i<=x.ilen;i++)
	{
		ret.a[i]+=3*x.a[i];
		ret.a[i+1]+=ret.a[i]/10;
		ret.a[i]%=10;
	}
	ret.ilen=x.ilen;
	while(ret.a[ret.ilen+1])
	{
		ret.ilen++;
	}
	return ret;
}
Bignum sub(Bignum x,Bignum y)
{
	Bignum ret=zero;
	for(int i=1;i<=y.ilen;i++)
	{
		ret.a[i]+=x.a[i]-y.a[i];
		if(ret.a[i]<0)
		{
			ret.a[i]+=10;
			ret.a[i+1]--;
		}
	}
	for(int i=y.ilen+1;i<=x.ilen;i++)
	{
		ret.a[i]+=x.a[i];
	}
	ret.ilen=x.ilen;
	while(!ret.a[ret.ilen]&&ret.ilen>1)
	{
		ret.ilen--;
	}
	return ret;
}
