				dp[i][j]=min(dp[i][j],dp[i+1][j-1]+((num[i]+num[j]>=3)?0:1)); // 如果两边的捆绑珠子加起来已经有3个了，就不用插入新的珠子了，否则要插入1个珠子 
			}
			for(int k=i;k<=j;k++) // 枚举中间点，注意区间可以只包含1个捆绑珠子 
			{
