#include<cmath>
#include<cstdio>
#include<cstring>
#include<iostream>
using namespace std;

int n,arr[505][2],dp[505][505],tmp; 

int main(){
    scanf("%d",&n);
    memset(dp,0x3f3f3f3f,sizeof(dp));
    arr[0][0]=-1;
    for(int i=1;i<=n;++i){
    	scanf("%d",&tmp);
        //合并同色段
    	if(tmp==arr[i-1][0]) arr[--i][1]++,n--;
    	else{
    		arr[i][0]=tmp;
    		arr[i][1]=1;
    	}
    }
    //初始化
    for(int i=1;i<=n;++i) dp[i][i]= arr[i][1]==1 ? 2 : 1;
    for(int i=1;i<n;++i){
    	for(int j=1;j<=n-i;++j){
        	//转移方程
    		if(arr[j][0]==arr[j+i][0]){
    			tmp=max(3-arr[j][1]-arr[j+i][1],0);
    			dp[j][j+i]=min(dp[j][j+i],dp[j+1][j+i-1]+tmp);
    		}
    		else{
    			for(int k=j;k<j+i;++k){
    				dp[j][j+i]=min(dp[j][j+i],dp[j][k]+dp[k+1][j+i]);
    			}
    		}
    	}
    }
    printf("%d",dp[1][n]);
    return 0;
}
