        if(ss[j]=='x') rem[i][j+1]=1;
    }

    int s=n*m*2+1,t=s+1;
    for(int i=1;i<=n;++i)
    for(int j=1;j<=m;++j)
    {
        if(rem[i][j]) continue;
        int u=(i-1)*m+j;
        add(s,u,inf); add(u+n*m,t,inf);//l=0,r=inf

        add(u,u+n*m,0);//l=1,r=1
        deg[u]--; deg[u+n*m]++;
    }

    for(int i=1;i<=n;++i)
    for(int j=1;j<=m;++j)
    {
        if(rem[i][j]) continue;
        int u=(i-1)*m+j;
        for(int k=0;k<2;++k)
        {
            int x=i+nX[k]*r,y=j+nY[k]*c,v=(x-1)*m+y;
            if(x>=1&&x<=n&&y>=1&&y<=m) 
            if(!rem[x][y]) add(u+n*m,v,inf);
        }
        if(r!=c)
        for(int k=0;k<2;++k)
        {
            int x=i+nX[k]*c,y=j+nY[k]*r,v=(x-1)*m+y;
            if(x>=1&&x<=n&&y>=1&&y<=m) 
            if(!rem[x][y]) add(u+n*m,v,inf);
        }
    }

    int ss=t+1,tt=ss+1;
    for(int i=1;i<=t;++i)
    {
        if(deg[i]>0) add(ss,i,deg[i]);
        else if(deg[i]<0) add(i,tt,-deg[i]);
    }
    add(t,s,inf);

    dicnic(ss,tt);
    int ans=E[tot].f;
    head[t]=E[head[t]].nxt;
    head[s]=E[head[s]].nxt;
    printf("%d",ans-dicnic(t,s));
    return 0;
}
