inline int Dinic(int x,int res) { //模板不讲 
	if(x==t) return res;
	int plus=res,k=0;
	for(int i=head[x]; i&&plus; i=edg[i].y) {
		if(edg[i].z&&d[edg[i].x]==d[x]+1) {
			int j=edg[i].x;
			k=Dinic(j,min(plus,edg[i].z));
			if(!k) d[j]=0;
			edg[i].z-=k;
			edg[i^1].z+=k;
			plus-=k;
		}
	}
	return res-plus;
}
