	    if(a[i][j]=='.')
	    {
	    	SUM++;
	    	int hh=pu(i,j),x,y;
	    	x=i+R,y=j+C;
			if(a[x][y]=='.'&&x>=1&&y>=1&&x<=n&&y<=m)
			add(hh,pu(x,y)+ff,1);
			x=i+R,y=j-C;
			if(a[x][y]=='.'&&x>=1&&y>=1&&x<=n&&y<=m)
			add(hh,pu(x,y)+ff,1);
			x=i+C,y=j+R;
			if(a[x][y]=='.'&&x>=1&&y>=1&&x<=n&&y<=m)
			add(hh,pu(x,y)+ff,1);
			x=i+C,y=j-R;
			if(a[x][y]=='.'&&x>=1&&y>=1&&x<=n&&y<=m)
			add(hh,pu(x,y)+ff,1);
			add(0,hh,1),add(hh+ff,t,1);
		}
	printf("%d",SUM-dinic());
