int n,k,m,diff,f[105][105],sum[105][105];
int a[105];
char c;
int main(){
	scanf("%d%d%d",&n,&k,&m);
	for(int i=1;i<=n;i++){
		c=getchar();
//		while(c!='0'&&c!='1')c=getchar();
		while(c=='\r'||c=='\n')c=getchar();
		a[i]=c-'0';
	}
	for(int i=1;i<=n;i++){
		c=getchar();
//		while(c!='0'&&c!='1')c=getchar();
		while(c=='\r'||c=='\n')c=getchar();
		if(a[i]!=c-'0')diff++;
	}
	for(int i=0;i<=n;i++)
	sum[i][0]=1;
	for(int i=1;i<=n;i++){
		for(int j=1;j<=i;j++)
		sum[i][j]=(sum[i-1][j]+sum[i-1][j-1])%MOD;
	}
	f[0][diff]=1;
