void exgcd(int a,int b ,int &x,int &y){

    if(!b){x=1,y=0;return;}
    exgcd(b,a%b,x,y);
    int t=x;
    x=y,y=t-(a/b)*y;
}
int inv(int a,int b){
    int x,y;
    return exgcd(a,b,x,y),(x%b+b)%b;
}
int ksm(int a,int b,int p){
    int ans=1;
    while(b){
        if(b&1)
            ans=a*ans%p;
        a=a*a%p;
        b>>=1;
    }
