	for(ll i=2;i*i<=n;i++){
		if(n%i==0){
			ll pk=1;
			while(n%i==0)pk*=i,n/=i;
			++num;
			number[num]=pk;
			prime[num]=i;
		}
	}
	if(n>1){++num;number[num]=n;prime[num]=n;}
}
