### Code:


```cpp
#include<iostream>
#include<cstdio>
#include<algorithm>
#include<cstring>
#include<cmath>
using namespace std;
#define G() Cr = getchar()
#define LL long long
LL Xr, Fr; char Cr;
inline LL rd() {
	Xr = 0, Fr = 1, G();
	while(Cr < '0' || Cr > '9') {if(Cr == '-') Fr = -1; G();}
	while(Cr >= '0' && Cr <= '9') Xr = (Xr<<1) + (Xr<<3) + (Cr&15), G();
	return Xr * Fr;
}
#define MAX_N 100005
#define oo 1000000007
int n, m, a, b, ans;
string ss;
int va[MAX_N], dp[MAX_N][27]; // i_th j min_ans
bool jd[30][30];
int main() {
	n = rd();
	cin >> ss;
	for( int i = 1; i <= n; i++ ) va[i] = ss[i-1] - 'a' + 1;
	m = rd();
	for( int i = 1; i <= m; i++ ) {
		cin >> ss;
		a = ss[0] - 'a' + 1, b = ss[1] - 'a' + 1;
		jd[a][b] = jd[b][a] = 1;
	}
	for( int i = 1; i <= n; i++ )
		for( int j = 0; j <= 26; j++ )
			dp[i][j] = oo;
	for( int i = 1; i <= n; i++ ) 
		for( int j = 0; j <= 26; j++ ) {
			dp[i][j] = min(dp[i][j], dp[i-1][j]+1);
			for( int p = 0; p <= 26; p++ )
				if(!jd[p][j] && va[i] == j ) dp[i][j] = min(dp[i][j], dp[i-1][p]);
		}
	ans = n;
	for( int i = 0; i <= 26; i++ ) ans = min(ans,dp[n][i]);
	cout << ans;
}
```

